svg.script:
var timerIncrement = 1000;
var block;
var sun;
function startClock(evt) {
  window.updateClock = updateClock
  block = evt.target.ownerDocument.getElementById("block");
  sun = evt.target.ownerDocument.getElementById("sun");
  updateClock();
}
function updateClock() {
  drawClock(new Time());
  setTimeout("updateClock()", timerIncrement);
}
function drawClock(time) {
  block.setAttribute("x", time.fiveMinuteInterval);
  block.setAttribute("y", time.hour);
  sun.setAttribute("cx", 12 * time.fraction);
  sun.setAttribute("cy", time.hour + time.fraction);
}

// I store time as hours, minutes, seconds, the fraction of the hour, and the five minute interval.
function Time() {
  var now = new Date();
  this.hour = now.getHours() % 12;
  this.fiveMinuteInterval = Math.floor(now.getMinutes() / 5);
  this.minutes = now.getMinutes();
  this.seconds = now.getSeconds();
  this.fraction = (this.minutes * 60 + this.seconds) / 3600;
}
